template.value:
umd(function(define) {
    define(function(require /*, exports, module */) {
        $END$
    });
}, "$browserGlobal$.$filename$", require, exports, module);

(function (define) {
    define(function(require /*, exports, module */) {
        $END$
    });
}((function() {
    if (umd.isRequireJS()) {
        // AMD.
        return define;
    } 
    else if (umd.isNodeJS()) {
        // Node (not CommonJS because module.exports does not conform)
        return function(factory) {
            var result = factory(require, exports, module);
            if (typeof result !== 'undefined') {
                module.exports = result;
            }
        };
    }
    else {
        // Browser globals
        return function(factory) {
            var result = factory(umd.require, exports, module);
            umd.namespace("$namespace$").$filename$ = (typeof result !== 'undefined')? result : module.exports;
        };
    }
}())));
